import React, { Component } from "react";
import "./projects.css";
import Header from "../header/header";
import Project from "../project/project";
import Carousel from "react-bootstrap/Carousel";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.js";
import classifyImg from "../../images/classify_logo.png";
import cellportImg from "../../images/classify_logo.png";
import hagertyImg from "../../images/hagerty_logo.png";

class Projects extends Component {
  state = {
    projects: [
      {
        id: "classify-app",
        name: "Classify App",
        description:
          "Mobile app connecting students with local classes, providing exposure to new fields without the commitment of semester-long courses.",
        tags: [
          "UI/UX Design",
          "Storyboarding",
          "Wireframing",
          "Prototyping",
          "Usability Testing",
        ],
        image: classifyImg,
        themeColor: "purple",
      },
      {
        id: "hdc-app",
        name: "Hagerty HDC App",
        description:
          "Connecting an overlooked user group to a company's developing business structure and culture.",
        tags: [
          "UX Design",
          "Persona Creation",
          "Wireframing",
          "Prototyping",
          "Business Analysis",
        ],
        image: hagertyImg,
        themeColor: "blue",
      },
      {
        id: "cellport",
        name: "CellPort Software",
        description:
          "Connecting an overlooked user group to a company's developing business structure and culture.",
        tags: [
          "UX Design",
          "Persona Creation",
          "Wireframing",
          "Prototyping",
          "Business Analysis",
        ],
        image: cellportImg,
        themeColor: "orange",
      },
    ],
  };
  render() {
    const { projects } = this.state;
    return (
      <>
        <Header />
        <Carousel interval={99999}>
          {projects.map((project) => (
            <Carousel.Item className="carousel-item">
              <Project
                className="project-slide d-block"
                key={project.id}
                project={project}
              ></Project>
            </Carousel.Item>
          ))}
        </Carousel>
      </>
    );
  }
}

export default Projects;
